package edu.unicen.experimenter;

import java.io.Serializable;
import java.util.Date;
import java.util.HashMap;

import javax.persistence.Entity;
import javax.persistence.GeneratedValue;
import javax.persistence.GenerationType;
import javax.persistence.Id;

import weka.ArfData;
import weka.datasetStrategy.GenerationStrategy;

/**
 * @author esteban
 * 
 */
@Entity
public class DataSet {

	/**
	 * Entity autogenerated id.
	 */
	@Id
	@GeneratedValue(strategy = GenerationType.AUTO)
	private Integer id;
	/**
	 * The date this dataset was created
	 */
	private Date creationDate;

	private GenerationStrategy generationStrategy;
	// Serialized
	private HashMap<String, Serializable> strategyOptions;

	private int numberOfInstances;

	private String[] attributes;

	private String classAttribute;

	private String beach;
	// serialized
	private ArfData data;

	/**
 * 
 */
	public DataSet() {
		// TODO Auto-generated constructor stub
	}

	/**
	 * @return
	 */
	public int getId() {
		return id;
	}

}
